import React, { useState, useEffect } from "react";
import Button from "../components/Button";
import { select03Data } from "../data/data";
import Card from "../components/Card";

export default function Select03() {
  const [selectedItem, setSelectedItem] = useState([]);
  const [activeId, setActiveId] = useState("");

  useEffect(() => {
    console.log("Newly selected item 3:", selectedItem);
    console.log("activeId 3", activeId);
  }, [selectedItem, activeId]);

  const handleSelectedItem = (obj) => {
    setActiveId(obj.id);
    const newItem = { ...obj };
    setSelectedItem(newItem);
  };

  return (
    <>
      <section className="bg-gray-50 dark:bg-gray-900">
        <div className="gap-6 flex flex-col items-center justify-center px-6 py-8 lg:py-0">
          <h1 className="text-xl font-sans font-bold leading-tight tracking-tight text-gray-900 mt-6 md:text-2xl dark:text-white">
            辞めさせたい意思がある場合は選択してください。
          </h1>

          <div className="w-full md:mt-0 sm:max-w-md xl:p-0">
            <div className="mb-4 py-2 space-y-4 bg-white rounded-lg shadow dark:border md:space-y-6 dark:bg-gray-800 dark:border-gray-700">
              <ul className="divide-y divide-gray-200 dark:divide-gray-700">
                {select03Data.map((data, i) => {
                  return (
                    <Card
                      key={i}
                      card={{ id: data.id.toString(), main: data.name, sub: "", image: data.image }}
                      onClick={() => handleSelectedItem(data)}
                      isActive={activeId === data.id ? true : false}
                    ></Card>
                  );
                })}
              </ul>
            </div>

            <Button btn={{ text: "次へ", color: "main", navigateTo: "comfirm" }}></Button>
            <Button btn={{ text: "戻る", color: "none", navigateTo: "select02" }}></Button>
          </div>
        </div>
      </section>
    </>
  );
}
